import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import ThemeToggle from './components/ThemeToggle';

export default function App() {
  const [collapsed, setCollapsed] = useState(false);
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');
  const navigate = useNavigate();

  useEffect(() => {
    const root = document.documentElement;
    if (theme === 'dark') root.classList.add('dark');
    else root.classList.remove('dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  return (
    <div className="min-h-screen flex">
      <Sidebar collapsed={collapsed} setCollapsed={setCollapsed} navigate={navigate} />
      <div className="flex-1 p-4">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-semibold">Lumibyte â€” Mini Chat</h1>
          <ThemeToggle theme={theme} setTheme={setTheme} />
        </div>
        <Routes>
          <Route path="/" element={<div className="p-6 card rounded-lg shadow">Start a new chat from the sidebar.</div>} />
          <Route path="/chat/:sessionId" element={<ChatWindow />} />
        </Routes>
      </div>
    </div>
  );
}
