export default function ThemeToggle({ theme, setTheme }) {
  return (
    <div className="flex items-center gap-3">
      <button onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')} className="px-3 py-1 rounded border">
        {theme === 'dark' ? 'Light' : 'Dark'}
      </button>
    </div>
  );
}
