import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function Sidebar({ collapsed, setCollapsed, navigate }) {
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    fetch('http://localhost:4000/api/sessions')
      .then(r => r.json())
      .then(data => setSessions(data.sessions || []))
      .catch(err => console.error(err));
  }, []);

  function newChat() {
    fetch('http://localhost:4000/api/new-chat')
      .then(r => r.json())
      .then(data => {
        navigate(`/chat/${data.id}`);
      });
  }

  return (
    <aside className={`w-72 ${collapsed ? 'hidden' : 'block'} bg-white dark:bg-[#071028] p-4 border-r`}>
      <div className="flex justify-between items-center mb-4">
        <div>
          <div className="font-bold">You</div>
          <div className="text-sm text-gray-500">family@local</div>
        </div>
        <button onClick={() => setCollapsed(s => !s)} aria-label="collapse" className="px-2 py-1 rounded border">â‰¡</button>
      </div>

      <button onClick={newChat} className="w-full mb-3 py-2 rounded bg-violet-500 text-white">New Chat</button>

      <div>
        <h3 className="text-sm font-medium mb-2">Sessions</h3>
        <ul>
          {sessions.map(s => (
            <li key={s.id} className="mb-2">
              <Link to={`/chat/${s.id}`} className="block p-2 rounded hover:bg-gray-100 dark:hover:bg-[#0b1530]">
                <div className="text-sm font-medium">{s.title || s.id}</div>
                <div className="text-xs text-gray-500">{s.history?.length || 0} messages</div>
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </aside>
  );
}
